import {
  assert,
  describe,
  test,
  clearStore
} from "matchstick-as"
import { Address, BigInt, Bytes } from "@graphprotocol/graph-ts"
import { handleMessageReceived, handleMessageSent } from "../src/l-1-usdc-message-transmitter"
import { createMessageReceivedEvent, createMessageSentEvent } from "./l-1-usdc-message-transmitter-utils"

// Tests structure (matchstick-as >=0.5.0)
// https://thegraph.com/docs/en/developer/matchstick/#tests-structure-0-5-0

describe("Message events", () => {
  test("MessageReceived created and stored", () => {
    const caller = Address.fromString(
      "0x0000000000000000000000000000000000000001"
    )
    const sender = Address.fromString(
      "0x0000000000000000000000000000000000000002"
    )
    const newMessageReceivedFromArb = createMessageReceivedEvent(caller, BigInt.fromI32(3), BigInt.fromI32(0), sender, Bytes.fromHexString(''))
    const newMessageReceivedFromAvalanche = createMessageReceivedEvent(caller, BigInt.fromI32(1), BigInt.fromI32(0), sender, Bytes.fromHexString(''))
    newMessageReceivedFromAvalanche.transaction.hash = Bytes.fromHexString('0xa16081f360e3847006db660bae1c6d1b2e17ec2b')

    handleMessageReceived(newMessageReceivedFromArb)
    handleMessageReceived(newMessageReceivedFromAvalanche)

    // MessageReceived from Avalanche is ignored
    assert.entityCount("MessageReceived", 1)
    assert.fieldEquals(
      "MessageReceived",
      "0xa16081f360e3847006db660bae1c6d1b2e17ec2a01000000",
      "sourceDomain",
      "3"
    )
    assert.fieldEquals(
      "MessageReceived",
      "0xa16081f360e3847006db660bae1c6d1b2e17ec2a01000000",
      "caller",
      caller.toHexString()
    )
    assert.fieldEquals(
      "MessageReceived",
      "0xa16081f360e3847006db660bae1c6d1b2e17ec2a01000000",
      "sender",
      sender.toHexString()
    )

    clearStore()
    // More assert options:
    // https://thegraph.com/docs/en/developer/matchstick/#asserts
  })

  test('MessageSent created and stored', () => {
    const newMessageSentToArb = createMessageSentEvent(Bytes.fromHexString(""))
    const newMessageSentToAvalanche = createMessageSentEvent(Bytes.fromHexString(""))
    newMessageSentToAvalanche.transaction.hash = Bytes.fromHexString('0xa16081f360e3847006db660bae1c6d1b2e17ec2b')
    
    // MessageSent from Avalanche is ignored
    handleMessageSent(newMessageSentToArb)
    handleMessageSent(newMessageSentToAvalanche)

    assert.entityCount("MessageSent", 1)
    assert.fieldEquals(
      "MessageSent",
      "0xa16081f360e3847006db660bae1c6d1b2e17ec2a01000000",
      "sender",
      "0xa16081f360e3847006db660bae1c6d1b2e17ec2a"
    )
  })
})
